mapper.select:
select count(*) FROM 
	(
	SELECT freelistenbook.*,freelisten.branchid FROM `freelistenbook`
	left JOIN freelisten 
	on freelisten.id = freelistenbook.fid
	) form1
	 LEFT JOIN address
	ON form1.branchid = address.aid
	where	qid=#{qid}
	
	
	
	
	and form1.tel= #{tel}

	select count(*) FROM 
		(
		SELECT freelistenbook.*,freelisten.branchid FROM `freelistenbook`
		left JOIN freelisten 
		on freelisten.id = freelistenbook.fid
		) form1
		 LEFT JOIN address
		ON form1.branchid = address.aid
		where qid=#{qid} 
		
		
		
		and form1.tel= #{tel}
		
		
			and status=#{status}

select form1.* FROM ( SELECT freelistenbook.*,freelisten.title,freelisten.imgurl, freelisten.fdesc,freelisten.status as listen_status, freelisten.pubtime,freelisten.branchid FROM `freelistenbook` left JOIN freelisten on freelisten.id = freelistenbook.fid ) form1 LEFT JOIN address ON form1.branchid = address.aid where qid=#{qid} and form1.tel= #{tel} and DATE_FORMAT(booktime, '%Y-%c-%d' )> STR_TO_DATE(#{bookTime},'%Y-%c-%d')
and status=#{status}
order by booktime desc

select count(*) FROM ( SELECT freelistenbook.*,freelisten.branchid FROM `freelistenbook` left JOIN freelisten on freelisten.id = freelistenbook.fid ) form1 LEFT JOIN address ON form1.branchid = address.aid where qid=#{qid}
and #{condition.fid}=fid
and userName like '%${condition.nickName}%'
and status = #{condition.status}
and DATE_FORMAT(booktime, '%Y-%c-%d' )>= STR_TO_DATE(#{condition.startDate},'%Y-%c-%d')
and DATE_FORMAT(booktime, '%Y-%c-%d' )<= STR_TO_DATE(#{condition.endDate},'%Y-%c-%d')

select form1.* FROM ( SELECT freelistenbook.*,freelisten.title,freelisten.imgurl, freelisten.fdesc,freelisten.status as listen_status, freelisten.pubtime,freelisten.branchid FROM `freelistenbook` left JOIN freelisten on freelisten.id = freelistenbook.fid ) form1 LEFT JOIN address ON form1.branchid = address.aid where qid=#{qid}
and #{condition.fid}=fid
and userName like '%${condition.nickName}%'
and status = #{condition.status}
and DATE_FORMAT(booktime, '%Y-%c-%d' )>= STR_TO_DATE(#{condition.startDate},'%Y-%c-%d')
and DATE_FORMAT(booktime, '%Y-%c-%d' )<= STR_TO_DATE(#{condition.endDate},'%Y-%c-%d')
limit #{page.start},#{page.end}
