mapper.select:
select id
from qiandao
where qid=#{qid} and uid=#{uid} and date=#{date}

mapper.update:
        update user
		set integral= integral + (select oneintegral
						  from integralvalue)
		where uid=#{uid} and qid=#{qid}

       	update user
		set integral= integral - (select buintegral
						  from integralvalue)
		where uid=#{uid} and qid=#{qid}

mapper.select:
select integral
from user
where uid=#{uid} and qid=#{qid}

select date
from qiandao
where uid=#{uid} and qid=#{qid} and date!=#{date}

select date
from qiandao
where uid=#{uid} and qid=#{qid}

mapper.update:
       	update user
		set integral= integral + (select sevenintegral
						  from integralvalue)
		where uid=#{uid} and qid=#{qid}
